INSERT INTO `buahan_shop` (`id`, `name`, `description`, `price`, `image`) VALUES
(1, 'Jeruk Medan', 'Jeruk Medan memiliki warna kulit hijau kekuningan seperti jeruk lokal pada umumnya. Jeruk Medan memiliki ciri rasa yang manis, kaya akan kandungan air serta bentuk bulir yang lebih besar.', 'Rp. 30.000/', 'https://d27xm72ryhvga.cloudfront.net/product/26_07_2022_02_47_25_jeruk_medan_kg.jpg'),
(2, 'Lemon', 'Lemon adalah buah-buahan citrus yang rasanya asam dan berwarna kuning terang. Buah ini kaya akan vitamin C dan antioksidan, serta memiliki banyak manfaat untuk kesehatan dan kecantikan.', 'Rp. 50.000/', 'https://fruits.today/image/cache/catalog/product/8/Cytryna_ES_05kg-800x800.jpg'),
(3, 'Sunkist', 'Sunkist adalah jenis jeruk manis (sweet orange) yang berasal dari California. Jeruk ini memiliki ukuran yang lebih besar, rasa manis yang menyegarkan, dan kaya akan manfaat kesehatan seperti vitamin C, folat, dan potasium. ', 'Rp. 50.000/', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSDtSLayPrQ6-ZplEMyxBLQOMybilXQ3zu0Ew&s'),
(4, 'Anggur Merah', 'Anggur merah kaya akan nutrisi dan antioksidan yang bermanfaat bagi kesehatan, termasuk vitamin C, vitamin B, kalium, mangan, dan resveratrol.', 'Rp. 60.000/', 'data:image/jpeg;base64,'),
(5, 'Pir Madu', 'Pir madu adalah jenis pir yang terkenal karena rasa manisnya yang khas, melebihi rasa pir biasa yang cenderung asam. Buah ini kaya akan serat dan berbagai nutrisi penting seperti vitamin C, vitamin B kompleks, asam folat, zat besi, kalium, riboflavin, dan kalsium. ', 'Rp. 28.000/', 'https://down-id.img.susercontent.com/file/1eae6e3d07ce8f1d263664fcfb82ff1c'),
(6, 'Apel Fuji', 'Apel Fuji adalah salah satu varietas apel yang sangat populer, dikenal dengan ukurannya yang besar, rasa manis madu, tekstur keras, segar, dan berair. Buah ini memiliki kulit merah kekuningan.', 'Rp. 45.000/', 'https://cdn.kerbel.in/assets/product/product_UFRARSRYYO_1683191416_1.webp'),
(7, 'Apel Merk Pohon', 'Apel dengan merk \"pohon\" (Apple Tree) umumnya merujuk pada varietas apel Fuji yang dikenal dengan kualitas premium dan rasa manis yang khas. Varietas ini populer di pasaran karena rasa manisnya yang lebih kuat, dagingnya yang renyah, dan tampilannya yang menarik. ', 'Rp. 70.000/', 'https://down-id.img.susercontent.com/file/sg-11134201-22120-4anvbaxxzdlv2a'),
(8, 'Kiwi Gold', 'Kiwi Gold, juga dikenal sebagai kiwi kuning atau kiwi Zespri SunGold, adalah varietas kiwi yang memiliki daging berwarna kuning cerah dan kulit yang halus, tidak berbulu, serta berwarna coklat keemasan. Rasanya lebih manis dan lembut daripada kiwi hijau (varietas Hayward).', 'Rp.150.000/', 'https://cdn.ralali.id/assets/img/Libraries/Buah-Kiwi-Gold-Zespri-1kg-Emas-1kg_h8kPwvcUGECI7lK3_1667375364.png'),
(9, 'Jeruk 春见 (Chun Jian)', 'Jeruk Chun Tian, juga dikenal sebagai Pa Pa Gan, adalah jenis jeruk premium dari Jepang yang diperkenalkan di Cina. Jeruk ini dikenal mudah dikupas tanpa perlu bantuan pisau. Dalam bahasa Mandarin, \"Chun Tian\" berarti musim semi, namun dalam konteks buah, merujuk pada varietas jeruk yang disebutkan di atas. ', 'Rp. 90.000/', 'https://www.glefruit.com/upload/product/1675756022559266.jpg'),
(10, 'Jeruk Santang Birma (jeruk mini)', 'Jeruk Santang Birma, juga dikenal sebagai jeruk mini, adalah jenis jeruk yang berasal dari Tiongkok dan populer di Indonesia karena rasanya yang manis dan ukurannya yang kecil. Jeruk ini sering disebut sebagai Jeruk Santang Madu karena rasanya yang manis seperti madu, dan sering dikonsumsi saat perayaan Imlek. ', 'Rp.50.000/', 'https://asset.kompas.com/crops/yHNh6CbQJfxUj8mBJT5B63mYo0w=/0x57:640x483/750x500/data/photo/2022/01/06/61d69e668e6c9.jpg'),
(11, 'Anggur Hitam Tanpa Biji', 'Anggur hitam tanpa biji (black seedless grapes) adalah varietas anggur yang tidak memiliki biji dan berwarna hitam. Varietas ini populer karena mudah dikonsumsi dan memiliki rasa manis yang lezat. ', 'Rp.110.000/', 'https://www.allfresh.co.id/media/images/product-thumbnails/anggur-2.jpg'),
(12, 'Anggur Hijau Muscat', 'Anggur Muscat hijau atau Shine Muscat dikenal karena rasanya yang manis, teksturnya yang renyah, dan aromanya yang segar, seperti madu. Buah ini juga dikenal tanpa biji dan seringkali dijual dengan harga yang lebih mahal karena rasa dan kualitasnya.', 'Rp. 90.000/', 'https://img-cdn.medkomtek.com/6WFRBU9Fui8zkw2-7yOkI1m7Ys4=/510x395/smart/filters:quality(100):format(webp)/article/KXgflilablVYRrvOEc9jh/original/m0dtu0watd3ryvkxh4p67t76m8tkfjgr.jpg'),
(13, 'Pir Jambu Madu', 'Pir jambu madu adalah sebutan untuk buah pir yang bentuknya mirip dengan buah jambu air dan memiliki rasa manis serta tekstur yang renyah. Buah ini kaya akan vitamin dan mineral yang bermanfaat bagi kesehatan, seperti vitamin C, vitamin A, serat, dan kalium.', 'Rp. 60.000/', 'https://asset.kompas.com/crops/qxIukwl4m3j9dLqH79d2X9cTils=/0x0:1275x850/750x500/data/photo/2022/02/04/61fc9dd89e736.jpg'),
(14, 'Pir Jambu', 'Pir Jambu adalah istilah populer yang merujuk pada jenis buah pir dengan warna kulit hijau dan bentuk yang menyerupai jambu biji (jambu merah). Lebih tepatnya, buah ini dikenal sebagai Pear Packham atau Pear Forrell. Pir Jambu memiliki rasa manis dan tekstur yang renyah. ', 'Rp. 50.000/', 'https://res.cloudinary.com/dk0z4ums3/image/upload/v1741760065/attached_image/pir-hijau-inilah-nutrisi-dan-manfaatnya-untuk-kesehatan.jpg'),
(15, 'Pir Xiang Lie', 'Pir Xiang Lie (atau Xiang Lie Pear) adalah jenis buah pir yang berasal dari China, dikenal dengan rasa manis dan segar, serta kadar air yang melimpah. Buah ini memiliki bentuk agak oval, dengan warna kulit hijau atau hijau kekuningan. ', 'Rp. 65.000/', 'https://ik.imagekit.io/dcjlghyytp1/08ef1b78b2c3033f29549b79e015c2d3?tr=f-auto,w-360'),
(16, 'Apel Gala', 'Apel Gala adalah sebuah kultivar apel (jenis apel) yang terkenal dengan rasa manis, tekstur renyah, dan aroma lembut yang khas. Ia adalah hasil persilangan antara varietas Kidd\'s Orange Red dan Golden Delicious, dan pertama kali muncul di Selandia Baru pada tahun 1930-an. ', 'Rp. 80.000/', 'https://drivethru.klikindomaret.com/tz0m/wp-content/uploads/sites/23/2021/09/apel-sweet-gala.jpg'),
(17, 'Apel Hijau', 'Apel hijau adalah buah yang populer dengan rasa asam yang khas. Selain rasanya, apel hijau juga memiliki berbagai manfaat kesehatan, antara lain: meningkatkan sistem kekebalan tubuh, menjaga kesehatan jantung, membantu pencernaan, dan berpotensi mencegah beberapa jenis kanker. ', 'Rp. 50.000/', 'https://static.honestdocs.id/system/blog_articles/main_hero_images/000/001/610/original/Manfaat_Apel_Hijau_dan_Kandungannya__Terbukti_Ilmiah!.jpg'),
(18, 'Jeruk Bali dari Aceh', 'Jeruk Bali, atau yang dikenal juga dengan nama Boh Giri di Aceh, adalah buah lokal yang populer di Aceh dan memiliki ukuran yang lebih besar dari jeruk pada umumnya. Jeruk Bali dikenal memiliki kulit yang tebal dan daging buah yang manis serta segar dengan sedikit rasa asam. ', 'Rp. 40.000/', 'https://asset.kompas.com/crops/NA9G5ConNPWL58wNBpv8_ewe2Wc=/0x0:1000x667/1200x800/data/photo/2023/09/25/651105b8b8dc1.jpg');

